import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
    const [reports, setReports] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        location: '',
        description: '',
    });

    // שליפת הדיווחים מהשרת
    useEffect(() => {
        axios.get('http://localhost:5000/api/reports')
            .then(response => setReports(response.data))
            .catch(error => console.error(error));
    }, []);

    // שליחת דיווח חדש
    const handleSubmit = (e) => {
        e.preventDefault();

        axios.post('http://localhost:5000/api/reports', formData)
            .then(response => {
                alert(response.data.message);
                setReports([...reports, response.data.report]);
                setFormData({ name: '', location: '', description: '' });
            })
            .catch(error => console.error(error));
    };

    return (
        <div>
            <h1>דיווחי חירום</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="שם"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="מיקום"
                    value={formData.location}
                    onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                />
                <textarea
                    placeholder="תיאור המקרה"
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                />
                <button type="submit">שלח דיווח</button>
            </form>

            <h2>רשימת דיווחים</h2>
            <ul>
                {reports.map(report => (
                    <li key={report.id}>
                        <strong>{report.name}</strong> | {report.location}
                        <p>{report.description}</p>
                        <small>{new Date(report.timestamp).toLocaleString()}</small>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
